mapper.update:
update course_selection
set normal_score = #{normalScore}, test_score = #{testScore}, total_score = #{totalScore}
where student_id = #{studentId}
and course_id = #{courseId}
and staff_id = #{teacherId}
and semester = #{semester}

mapper.select:
SELECT cl.course_id, c.course_name, cl.class_time, cl.class_id, cl.semester
FROM class cl
JOIN course c ON cl.course_id = c.course_id
WHERE cl.staff_id = #{teacherId}

    AND cl.semester = #{semester}  


    AND cl.course_id = #{courseId}

select cs.student_id, cs.course_id, c.course_name, s.name, cs.normal_score, cs.test_score, cs.total_score, cs.class_id
from course_selection cs
join student s on cs.student_id = s.student_id
join course c on cs.course_id = c.course_id
where cs.staff_id = #{teacherId}

    AND cs.semester = #{semester}  


    AND cs.course_id = #{courseId}  


    AND cs.class_id = #{classId}  

order by cs.student_id
